contracts/core/AegisCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../interfaces/IVerifier.sol";
import "../interfaces/IUnstoppable.sol";
import "./BackstopPool.sol";

contract AegisCore is AccessControl, Pausable {
    bytes32 public constant SENTINEL_ROLE = keccak256("SENTINEL_ROLE");
    bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");

    enum RiskState { GREEN, YELLOW, RED }
    RiskState public currentState;

    IVerifier public verifier;
    IUnstoppable public identityRegistry;
    BackstopPool public backstopPool;

    event GlobalRiskCondition(bool active, uint256 timestamp, uint256 riskScore);
    event RiskStateChanged(RiskState oldState, RiskState newState);

    constructor(
        address _verifier, 
        address _identityRegistry, 
        address _backstopPool,
        address _sentinel,
        address _governor
    ) {
        verifier = IVerifier(_verifier);
        identityRegistry = IUnstoppable(_identityRegistry);
        backstopPool = BackstopPool(_backstopPool);

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SENTINEL_ROLE, _sentinel);
        _grantRole(GOVERNOR_ROLE, _governor);

        currentState = RiskState.GREEN;
    }

    // State Transitions
    function setRiskState(RiskState _newState, bytes memory proof, bytes32 inputHash) external {
        require(hasRole(SENTINEL_ROLE, msg.sender) || hasRole(GOVERNOR_ROLE, msg.sender), "Caller is not authorized");

        if (_newState == RiskState.RED) {
             // Require ZK-proof verification for RED mode transition
             require(verifier.verify(proof, inputHash), "Invalid ZK proof for Red Mode");
             emit GlobalRiskCondition(true, block.timestamp, 100);
        } else {
             // If transitioning out of RED or to YELLOW/GREEN, we might emit risk condition false
             emit GlobalRiskCondition(false, block.timestamp, 0);
        }

        RiskState oldState = currentState;
        currentState = _newState;

        emit RiskStateChanged(oldState, _newState);

        if (_newState == RiskState.RED) {
            _pause();
        } else {
            _unpause();
        }
    }
}

contracts/core/AegisLens.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IUnstoppable.sol";
import "../interfaces/IAegisOracle.sol";
import "./AegisPool.sol";

/**
 * @title AegisLens
 * @notice Pure View Logic for the Frontend "Cockpit".
 */
contract AegisLens {
    IUnstoppable public identityRegistry;
    IAegisOracle public oracle;

    constructor(address _identityRegistry, address _oracle) {
        identityRegistry = IUnstoppable(_identityRegistry);
        oracle = IAegisOracle(_oracle);
    }

    struct UserAccountData {
        uint256 totalCollateralUSD;
        uint256 totalDebtUSD;
        uint256 availableBorrowsUSD;
        uint256 currentLiquidationThreshold;
        uint256 ltv;
        uint256 healthFactor;
        uint256 liquidationPrice;
    }

    /**
     * @notice Convenience function to fetch balances and calculate risk in one call.
     */
    function getUserPosition(address pool, address user) external view returns (UserAccountData memory) {
        // Fetch balances from Pool
        uint256 col = AegisPool(pool).userCollateralETH(user);
        uint256 debt = AegisPool(pool).userDebtUSDC(user);

        // Delegate to calculation logic
        return getUserAccountData(user, col, debt);
    }

    /**
     * @notice Calculates the complete risk profile for a user given their balances.
     * @param user The user address (to check Badge/Risk Params)
     * @param collateralETH Amount of ETH collateral (wei)
     * @param debtUSDC Amount of USDC debt (18 decimals)
     */
    function getUserAccountData(
        address user, 
        uint256 collateralETH, 
        uint256 debtUSDC
    ) public view returns (UserAccountData memory) {

        // 1. Get Risk Parameters based on Badge (Whale, Ancient One, etc.)
        IUnstoppable.RiskParams memory params = identityRegistry.getRiskParameters(user);

        // 2. Value Calculation
        uint256 price = oracle.getLatestPrice(address(0));
        uint256 collateralValue = (collateralETH * price) / 1e18; // USD value 18 decimals
        uint256 debtValue = debtUSDC;

        // 3. Thresholds
        // LTV is in %, e.g. 80.
        uint256 maxBorrow = (collateralValue * params.ltv) / 100;

        uint256 available = 0;
        if (maxBorrow > debtValue) {
            available = maxBorrow - debtValue;
        }

        // 4. Health Factor
        // HF = (Collateral * LTV) / Debt
        // If Debt is 0, HF is max
        uint256 hf = type(uint256).max;
        if (debtValue > 0) {
            hf = (collateralValue * params.ltv * 1e18) / (debtValue * 100); 
            // Note: multiplied by 1e18 for precision, divided by 100 for percentage
        }

        // 5. Liquidation Price
        // Price where Collateral * LTV = Debt
        // Debt = (CollateralAmount * Price * LTV) / 100
        // Price = (Debt * 100) / (CollateralAmount * LTV)
        uint256 liqPrice = 0;
        if (collateralETH > 0 && params.ltv > 0) {
            liqPrice = (debtValue * 100 * 1e18) / (collateralETH * params.ltv);
        }

        return UserAccountData({
            totalCollateralUSD: collateralValue,
            totalDebtUSD: debtValue,
            availableBorrowsUSD: available,
            currentLiquidationThreshold: params.ltv,
            ltv: params.ltv,
            healthFactor: hf,
            liquidationPrice: liqPrice
        });
    }
}

contracts/core/AegisPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./AegisCore.sol";
import "./BackstopPool.sol";
import "../interfaces/IUnstoppable.sol";
import "../interfaces/IAegisOracle.sol";

/**
 * @title AegisPool
 * @notice The "Casino". User-facing lending pool.
 * Handles Deposits (ETH) and Borrows (USDC).
 * Enforces Risk Logic defined by AegisCore and IdentityRegistry.
 */
contract AegisPool is ReentrancyGuard {
    AegisCore public aegisCore;
    IERC20 public usdcToken;
    IUnstoppable public identityRegistry;
    IAegisOracle public oracle;

    // User State
    mapping(address => uint256) public userCollateralETH;
    mapping(address => uint256) public userDebtUSDC;
    mapping(address => uint256) public probationStart;

    // LP State
    mapping(address => uint256) public liquidityProviderBalance;

    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);
    event Borrow(address indexed user, uint256 amount);
    event Repay(address indexed user, uint256 amount);
    event ProvideLiquidity(address indexed provider, uint256 amount);
    event WithdrawLiquidity(address indexed provider, uint256 amount);
    event Liquidated(address indexed user, address indexed liquidator, uint256 debtRepaid, uint256 collateralSeized);
    event ProbationStarted(address indexed user, uint256 timestamp);

    constructor(
        address _aegisCore,
        address _usdcToken,
        address _identityRegistry,
        address _oracle
    ) {
        aegisCore = AegisCore(_aegisCore);
        usdcToken = IERC20(_usdcToken);
        identityRegistry = IUnstoppable(_identityRegistry);
        oracle = IAegisOracle(_oracle);
    }

    // --- User Actions ---

    /**
     * @notice Deposit ETH as collateral.
     */
    function depositETH() external payable nonReentrant {
        require(msg.value > 0, "Amount must be > 0");
        userCollateralETH[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);
    }

    /**
     * @notice Withdraw ETH collateral.
     * Checks health factor after withdrawal.
     */
    function withdrawETH(uint256 amount) external nonReentrant {
        require(userCollateralETH[msg.sender] >= amount, "Insufficient balance");

        userCollateralETH[msg.sender] -= amount;

        // Health Check
        _validateHealth(msg.sender);

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");

        emit Withdraw(msg.sender, amount);
    }

    /**
     * @notice Borrow USDC against ETH collateral.
     * BLOCKED if RiskState is YELLOW or RED.
     */
    function borrowUSDC(uint256 amount) external nonReentrant {
        // 1. Check Protocol State
        AegisCore.RiskState state = aegisCore.currentState();
        require(state == AegisCore.RiskState.GREEN, "Protocol in Risk Mode: Borrowing Paused");

        // 2. Accounting
        userDebtUSDC[msg.sender] += amount;

        // 3. Health Check
        _validateHealth(msg.sender);

        // 4. Transfer
        require(usdcToken.transfer(msg.sender, amount), "Transfer failed");

        emit Borrow(msg.sender, amount);
    }

    /**
     * @notice Repay USDC debt.
     */
    function repayUSDC(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(userDebtUSDC[msg.sender] >= amount, "Overpayment");

        // Transfer From User
        require(usdcToken.transferFrom(msg.sender, address(this), amount), "Transfer From failed");

        userDebtUSDC[msg.sender] -= amount;

        emit Repay(msg.sender, amount);
    }

    function withdrawLiquidity(uint256 amount) external nonReentrant {
        require(liquidityProviderBalance[msg.sender] >= amount, "Insufficient LP balance");
        // Ensure pool has liquidity (might be borrowed out)
        uint256 available = usdcToken.balanceOf(address(this));
        require(available >= amount, "Pool utilization too high");

        liquidityProviderBalance[msg.sender] -= amount;
        require(usdcToken.transfer(msg.sender, amount), "Transfer failed");
        emit WithdrawLiquidity(msg.sender, amount);
    }

    /**
     * @notice Standard Liquidation Function (Green/Yellow Modes).
     * @param user The borrower address.
     * @param debtToCover Amount of USDC debt to repay.
     */
    function liquidate(address user, uint256 debtToCover) external nonReentrant {
        // 1. State Checks
        AegisCore.RiskState state = aegisCore.currentState();
        require(state != AegisCore.RiskState.RED, "Red Mode: Use Sponge");

        uint256 debt = userDebtUSDC[user];
        require(debtToCover <= debt, "Amount exceeds debt");
        require(debtToCover > 0, "Amount must be > 0");

        // 2. Health & Solvency Check
        (uint256 healthFactor, uint256 collateralValue) = _calculateHealth(user);

        // Yellow Mode Rule: "Only deeply insolvent positions"
        if (state == AegisCore.RiskState.YELLOW) {
            // Threshold: HF < 0.95 (Deep)
            // HF is 18 decimals. 0.95 * 1e18 = 95e16
            require(healthFactor < 950000000000000000, "Yellow Mode: Not deeply insolvent");
        } else {
             require(healthFactor < 1e18, "User is healthy");
        }

        // 3. Grace Period - "Two-Step Liquidation"
        IUnstoppable.RiskParams memory params = identityRegistry.getRiskParameters(user);
        if (params.gracePeriod > 0) {
            uint256 start = probationStart[user];
            if (start == 0) {
                // First time detected: Trigger Probation
                probationStart[user] = block.timestamp;
                emit ProbationStarted(user, block.timestamp);
                return; // EXIT. Do not liquidate yet.
            } else {
                // Check expiry
                require(block.timestamp > start + params.gracePeriod, "In Grace Period");
                // Reset probation after successful liquidation (optional, or keep distinct)
                probationStart[user] = 0; 
            }
        }

        // 4. Execution
        // Liquidator pays Debt (USDC)
        require(usdcToken.transferFrom(msg.sender, address(this), debtToCover), "Transfer From failed");
        userDebtUSDC[user] -= debtToCover;

        // Validating Seize Amount
        // Bonus (Liquidation Penalty). Params has e.g. 10 (10%).
        // Seize Value = DebtPaid * (1 + Penalty/100)
        // Seize ETH = Seize Value / ETH Price

        uint256 penalty = params.liquidationPenalty; // e.g. 10
        uint256 bonusMultiplier = 100 + penalty; // 110

        uint256 price = oracle.getLatestPrice(address(0)); // 18 decimals

        // Debt Value (USDC 18 dec) * Multiplier / 100
        // Converted to ETH: Value / Price

        uint256 seizeValue = (debtToCover * bonusMultiplier) / 100; // USD Value
        uint256 seizeETH = (seizeValue * 1e18) / price; 

        require(userCollateralETH[user] >= seizeETH, "Collateral insufficient for bonus");

        userCollateralETH[user] -= seizeETH;

        // Transfer Collateral to Liquidator
        (bool success, ) = msg.sender.call{value: seizeETH}("");
        require(success, "ETH Transfer failed");

        emit Liquidated(user, msg.sender, debtToCover, seizeETH);

        // Refund Logic: If debt is fully cleared, return remaining collateral to user
        if (userDebtUSDC[user] == 0) {
            uint256 remaining = userCollateralETH[user];
            if (remaining > 0) {
                userCollateralETH[user] = 0;
                (bool refundSuccess, ) = user.call{value: remaining}("");
                require(refundSuccess, "Refund to user failed");
            }
        }
    }

    // --- Internal Safety ---

    function _calculateHealth(address user) internal view returns (uint256 hf, uint256 colVal) {
        uint256 collateral = userCollateralETH[user];
        uint256 debt = userDebtUSDC[user];
        if (debt == 0) return (type(uint256).max, 0);

        uint256 price = oracle.getLatestPrice(address(0));
        uint256 collateralValue = (collateral * price) / 1e18;
        colVal = collateralValue;

        IUnstoppable.RiskParams memory params = identityRegistry.getRiskParameters(user);
        // HF = (Collateral * LTV) / Debt
        hf = (collateralValue * params.ltv * 1e18) / (debt * 100);
    }

    function _validateHealth(address user) internal view {
        (uint256 hf, ) = _calculateHealth(user);
        require(hf >= 1e18, "Health Factor < 1.0");
    }

    // --- Liquidator Interface (For Backstop) ---
    /**
     * @notice Allows the Backstop/Sponge to Seize Collateral and Repay Debt on behalf of a user.
     * Only callable by BackstopPool.
     */
    function seizeForBackstop(address user, uint256 debtRepaid) external {
        // Access Control: Only the registered Backstop Pool can call this
        require(msg.sender == address(aegisCore.backstopPool()), "Caller is not Backstop Pool");

        // Logic: Backstop has paid the debt (via atomic swap or direct transfer elsewhere)
        // We update accounting here.

        require(userDebtUSDC[user] >= debtRepaid, "Debt too small");
        userDebtUSDC[user] -= debtRepaid;

        // Seize 110% of value? Or just value?
        // Let's settle for simple swap logic.
        // Backstop logic handles calculations.
    }
}

contracts/core/BackstopPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/IERC1271.sol";
import "../interfaces/ICoWSwap.sol";
import "../interfaces/IUnstoppable.sol";

contract BackstopPool is ReentrancyGuard, IERC1271 {
    address public aegisCore;
    IERC20 public usdcToken;
    ICoWSwap public cowSwap;
    IUnstoppable public identityRegistry;
    mapping(address => uint256) public probationStart;

    event ProbationStarted(address indexed borrower, uint256 timestamp);
    event CollateralSeized(address indexed borrower, uint256 collateralAmount, uint256 debtAbsorbed);
    event DarkPoolRouteActive(address indexed borrower, uint256 amount);

    modifier onlyCoordinator() {
        require(msg.sender == aegisCore, "Only Aegis Core can initiate absorption");
        _;
    }

    modifier onlyVerifiedAgent() {
        string memory name = identityRegistry.reverseNameOf(msg.sender);
        // Simple "Contains/EndsWith" check for Hackathon
        // Check if name has ".agent" or "agent" suffix/substring
        bool isAgent = _contains(name, "agent") || _contains(name, "Agent");
        require(isAgent, "Caller is not a Verified Agent");
        _;
    }

    function _contains(string memory where, string memory what) internal pure returns (bool) {
        bytes memory whereBytes = bytes(where);
        bytes memory whatBytes = bytes(what);
        if (whatBytes.length > whereBytes.length) return false;

        // Simple substring check
        for (uint i = 0; i <= whereBytes.length - whatBytes.length; i++) {
            bool found = true;
            for (uint j = 0; j < whatBytes.length; j++) {
                if (whereBytes[i + j] != whatBytes[j]) {
                    found = false;
                    break;
                }
            }
            if (found) return true;
        }
        return false;
    }

    constructor(address _usdcToken, address _cowSwap, address _identityRegistry) {
        usdcToken = IERC20(_usdcToken);
        cowSwap = ICoWSwap(_cowSwap);
        identityRegistry = IUnstoppable(_identityRegistry);
    }

    function setAegisCore(address _aegisCore) external {
        require(aegisCore == address(0), "Core already set");
        aegisCore = _aegisCore;
    }

    function triggerProbation(address borrower) external onlyVerifiedAgent {
        IUnstoppable.RiskParams memory params = identityRegistry.getRiskParameters(borrower);
        require(params.gracePeriod > 0, "No grace period for user");

        probationStart[borrower] = block.timestamp;
        emit ProbationStarted(borrower, block.timestamp);
    }

    // EIP-1271 Implementation
    function isValidSignature(bytes32 _hash, bytes memory _signature) external view override returns (bytes4) {
        return 0x1626ba7e;
    }

    function absorbPosition(address borrower, uint256 collateralAmount, uint256 debtAmount) external nonReentrant onlyCoordinator {
        // 1. Eligibility Check (Health Factor)
        IUnstoppable.RiskParams memory params = identityRegistry.getRiskParameters(borrower);

        uint256 healthScore = (collateralAmount * params.ltv);
        uint256 debtThreshold = debtAmount * 100; // 2 decimals precision adjustment

        require(healthScore < debtThreshold, "User is Solvable (HF >= 1.0)");

        // 2. Grace Period Check
        if (params.gracePeriod > 0) {
            uint256 start = probationStart[borrower];
            require(start > 0, "Probation not started");
            require(block.timestamp > start + params.gracePeriod, "In Grace Period");
        }

        uint256 spongeBalance = usdcToken.balanceOf(address(this));

        // 3. The "Insolvency Paradox" Solution
        if (spongeBalance >= debtAmount) {
            // Branch A: Sponge Full - Atomic Swap
            // IMPROVEMENT: Actually spend the sponge's capital to reflect insolvency absorption
            // Sending to 0xdead to simulate burning/paying off the protocol debt
            usdcToken.transfer(address(0x000000000000000000000000000000000000dEaD), debtAmount);

            emit CollateralSeized(borrower, collateralAmount, debtAmount);
        } else {
            // Branch B: Sponge Empty - Route to Dark Pool
            cowSwap.placeOrder(address(0), collateralAmount); 

            emit DarkPoolRouteActive(borrower, collateralAmount);
        }
    }
}

contracts/core/IdentityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IUnstoppable.sol";

// Minimal interface for Unstoppable Domains Registry (UNS)
interface IUDRegistry {
    function reverseNameOf(address addr) external view returns (string memory);
}

contract IdentityRegistry is IUnstoppable {
    IUDRegistry public udRegistry;

    constructor(address _udRegistry) {
        udRegistry = IUDRegistry(_udRegistry);
    }

    function reverseNameOf(address user) external view override returns (string memory) {
        try udRegistry.reverseNameOf(user) returns (string memory name) {
            return name;
        } catch {
            return "";
        }
    }

    function getRiskParameters(address user) external view override returns (RiskParams memory) {
        // 1. Reverse Resolution: Get domain from UD
        // In a real scenario, this resolves '0xUser' -> 'whale.crypto'
        string memory domainName = "";

        try udRegistry.reverseNameOf(user) returns (string memory name) {
            domainName = name;
        } catch {
            // Fallback if no reverse record exists
            return _defaultParams();
        }

        bytes32 domainHash = keccak256(bytes(domainName));

        // 2. Table 1 Rules Application
        // Parsing the "Badge" from the domain name. 
        // For this architecture, we assume the domain *is* the badge identifier 
        // or contains it. (e.g. "ancient-one" or "whale").

        // "Ancient One" Badge
        if (domainHash == keccak256(bytes("ancient-one")) || domainHash == keccak256(bytes("Ancient One"))) {
            return RiskParams({
                ltv: 80,             // 80%
                liquidationPenalty: 8, // 8%
                gracePeriod: 900     // 15 minutes
            });
        }

        // "Whale" Badge
        if (domainHash == keccak256(bytes("whale")) || domainHash == keccak256(bytes("Whale"))) {
            return RiskParams({
                ltv: 82,             // 82%
                liquidationPenalty: 5, // 5%
                gracePeriod: 1800    // 30 minutes
            });
        }

        // "Clean Sheet" Badge
        if (domainHash == keccak256(bytes("clean-sheet")) || domainHash == keccak256(bytes("Clean Sheet"))) {
            return RiskParams({
                ltv: 77,             // 75% + 2% Bonus
                liquidationPenalty: 10,
                gracePeriod: 0
            });
        }

        // Default Risk Profile
        return _defaultParams();
    }

    function _defaultParams() internal pure returns (RiskParams memory) {
        return RiskParams({
            ltv: 75,
            liquidationPenalty: 10,
            gracePeriod: 0
        });
    }
}

